import React, { Component } from "react";
import "./index.css";
import todosList from "./todos.json";


class App extends Component {
  state = {
    todos: todosList
  };
  handleDeleteAllTodo = (event, todoIdToDeleteAll) => {
    console.log("Delete All");
    const newToDoAllList = this.state.todos.filter(todo => {
      if(todo.completed === true){
      return false;
      }
      return true;
    });

    this.setState({ todos: newToDoAllList });
  };

  handleDeleteTodo = (event, todoIdToDelete) => {
    console.log("Deleted");
    const newToDoList = this.state.todos.filter(todo => {
      if (todo.id === todoIdToDelete) {
        return false;
      }
      return true;
    });

    this.setState({ todos: newToDoList });
  };
  //if you just return false^^ and no true then it will delete all of the to-dos do not need an if statement just to return false in newToDoList.filter(todo => { return false}

  handleToggleComplete = (event, todoIdToToggle) => {
    const newTodos = this.state.todos.slice();
    const newnewTodos = newTodos.map(todo => {
      if (todo.id === todoIdToToggle) {
        todo.completed = !todo.completed;
      }
      return todo;
    });
    this.setState({ todos: newnewTodos });
  };

  handleAddToDo = event => {
    if (event.key === "Enter") {
      const newTodo = {
        userId: 1,
        id: Math.floor(Math.random() * 1000000),
        title: event.target.value,
        completed: false
      };

      const newTodos = this.state.todos.slice();
      newTodos.push(newTodo);
      this.setState({ todos: newTodos });
      event.target.value = "";
    }
  };

  render() {
    return (
      <section className="todoapp">
        <header className="header">
          <h1>todos</h1>
          <input
            className="new-todo"
            placeholder="What needs to be done?"
            onKeyDown={this.handleAddToDo}
            autoFocus
          />
        </header>
        <TodoList
          todos={this.state.todos}
          handleToggleComplete={this.handleToggleComplete}
          handleDeleteTodo={this.handleDeleteTodo}
          //handleDeleteAllTodo={this.handleDeleteAllTodo}
        />
        <footer className="footer">
          {/* <!-- This should be `0 items left` by default --> */}
          <span className="todo-count">
            <strong>0</strong> item(s) left
          </span>
          <ul className="filters">
            <li>
              <a href="/">All</a>
            </li>
            <li>
              <a href="/active">Active</a>
            </li>
            <li>
              <a href="/completed">Completed</a>
            </li>
          </ul>
          <button
            className="clear-completed"
            onClick={this.handleDeleteAllTodo}
          >
            Clear completed
          </button>
        </footer>
      </section>
    );
  }
}
//button onClick={this.handleDeleteAllTodo}




export default App;
